using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Windows.Media.Imaging;

namespace Checkers.Utils
{
    public class PieceBuilder
    {
        private static readonly string RED_KING = "";
        private static readonly string RED_PIECE = "";
        private static readonly string WHITE_KING = "";
        private static readonly string WHITE_PIECE = "";
       

        public static BitmapImage GetPieceImage(byte typeOfPiece)
        {
            byte[] imageBytes = null;

            if(typeOfPiece == 1)
            {
                imageBytes = Convert.FromBase64String(WHITE_PIECE);
            } 
            else if (typeOfPiece == 2)
            {
                imageBytes = Convert.FromBase64String(RED_PIECE);
            } 
            else if (typeOfPiece == 3)
            {
                imageBytes = Convert.FromBase64String(WHITE_KING);
            }
            else if(typeOfPiece == 4)
            {
                imageBytes = Convert.FromBase64String(RED_KING);
            }
            else
            {
                throw new ArgumentException("Invalid type of piece");
            }

            using (MemoryStream ms = new MemoryStream(imageBytes))
            {
                BitmapImage bitmapImage = new BitmapImage();
                bitmapImage.BeginInit();
                bitmapImage.StreamSource = ms;
                bitmapImage.CacheOption = BitmapCacheOption.OnLoad;
                bitmapImage.EndInit();
                return bitmapImage;
            }
        }
    }
}
